import React, { useState } from 'react';
import { Container, Link, TextBox } from './Board.styled';
import { useTheme } from '@mui/material';
import AddBoardModal from 'components/Modals/AddBoardModal/AddBoardModal';

const Board = () => {
  const [isModalOpen, setIsModalOpen] = useState(false); // State to control modal visibility

  const handleSubmit = data => {
    addBoard({ data });
  };

  const theme = useTheme();

  const openModal = () => {
    setIsModalOpen(true);
  };

  const closeModal = () => {
    setIsModalOpen(false);
  };

  return (
    <Container>
      <TextBox theme={theme}>
        Before starting your project, it is essential{' '}
        <Link theme={theme} onClick={openModal}>
          to create a board
        </Link>{' '}
        to visualize and track all the necessary tasks and milestones. This
        board serves as a powerful tool to organize the workflow and ensure
        effective collaboration among team members.
      </TextBox>
      <AddBoardModal isOpen={isModalOpen} onClose={closeModal} />
    </Container>
  );
};

export default Board;
