export const Icon = styled.svg`
  width: 14px;
  height: 14px;
  stroke: #fff;
`;
export const UserButton = styled.button`
  position: absolute;
  bottom: -20%;
  left: 38%;
  display: flex;
  align-items: center;
  justify-content: center;
  border-radius: 8px;

  padding: 0;
  width: 28px;
  height: 28px;
  border: none;
  background-color: #161616;

  transition: all 150ms linear;
  cursor: pointer;

  &:hover {
    opacity: 0.8;
  }
`;
