// Ваш компонент App

import { useEffect, useState } from 'react';
import { Routes, Route } from 'react-router-dom';
import Layout from 'components/Layout/Layout';
import HomePage from 'pages/Home/Home';
import ScreensPage from 'pages/Home/ScreensPage/ScreensPage';
import WelcomePage from 'pages/WelcomePg/WelcomePage';
import AuthPage from 'components/Auth/Auth';
import LogIn from 'components/Auth/LogInPg/LogInPg';
import Registration from 'components/Auth/RegistrationPg/RegistrationPg';
import PrivateRoute from 'components/Routes/PrivateRoute';
import PublicRoute from 'components/Routes/PublicRoute';
import Loader from 'components/Loader/Loader';
import { useSelector, useDispatch } from 'react-redux';
import { refreshUser } from '../../redux/authorization/authReducer';
import { selectIsRefreshing } from '../../redux/selectors';
import { ThemeProvider } from 'styled-components';
import { darkTheme } from '../../assets/theme/theme';

const App = () => {
  const [isNewBoardAdded, setIsNewBoardAdded] = useState(false); // Додайте стан для відстеження успішності додавання борда
  const dispatch = useDispatch();
  const isRefreshing = useSelector(selectIsRefreshing);

  useEffect(() => {
    dispatch(refreshUser());
  }, [dispatch]);

  const handleAddBoardSuccess = () => {
    setIsNewBoardAdded(true); // Оновіть стан після успішного додавання борда
  };

  return (
    <>
      <ThemeProvider theme={darkTheme}>
        {isRefreshing ? (
          <Loader />
        ) : (
          <Routes>
            <Route path="/" element={<Layout />}>
              <Route
                index
                element={
                  <PublicRoute redirectTo="/home" component={<WelcomePage />} />
                }
              />
              <Route
                path="/home"
                element={
                  <PrivateRoute
                    redirectTo="/"
                    component={
                      isNewBoardAdded ? <HomePage /> : <ScreensPage /> // Використовуйте умовний рендеринг
                    }
                  />
                }
              >
                <Route path=":boardName" element={<ScreensPage />} />
              </Route>
              <Route
                path="auth/:id"
                element={
                  <PublicRoute redirectTo="/" component={<AuthPage />} />
                }
              >
                <Route path="login" element={<LogIn />} />
                <Route path="registration" element={<Registration />} />
              </Route>
            </Route>
          </Routes>
        )}
      </ThemeProvider>
    </>
  );
};

export default App;
