import { registrationSchema } from '../../components/validation/schema';
import { useState } from 'react';
import { useForm } from 'react-hook-form';
import { useDispatch } from 'react-redux';
import { Eye } from 'assets/fonts/images/icons/Eye';
import { EyeSlash } from 'assets/fonts/images/icons/EyeSlash';
import { registration } from 'redux/authorization/authReducer';
import { toast } from 'react-toastify';

import { Wrap, Input, Button, Form, Error } from './RegistrationPg.styled';

const Registration = () => {
  const [showPassword, setShowPassword] = useState(false);
  const dispatch = useDispatch();

  const initialValues = {
    name: '',
    email: '',
    password: '',
  };

  const {
    handleSubmit,

    formState: { errors, isValid, touched },
    values,
  } = useForm({
    defaultValues: initialValues,
    registrationSchema,
  });

  const swapPassword = () => {
    setShowPassword(!showPassword);
  };

  const onSubmit = (values, { resetForm }) => {
    const { name, email, password } = values;
    dispatch(
      registration({
        name,
        email,
        password,
      })
    );
    resetForm();
  };

  return (
    <>
      <Form onSubmit={handleSubmit(onSubmit)}>
        <Input
          type="text"
          placeholder="Enter your name"
          id="name"
          name="name"
          value={values && values.name}
          $error={touched && touched.name && errors.name}
        />
        {errors.name && <Error>{errors.name.message}</Error>}

        <Input
          type="email"
          id="email"
          name="email"
          placeholder="Enter your email"
          value={values && values.email}
          $error={touched && touched.email && errors.email}
        />
        {errors.email && <Error>{errors.email.message}</Error>}

        <Wrap>
          <Input
            type="password"
            id="password"
            name="password"
            placeholder="Create a password"
            value={values && values.password}
            $error={touched && touched.password && errors.password}
          />
          <button type="button" onClick={swapPassword}>
            {showPassword ? <Eye /> : <EyeSlash />}
          </button>
        </Wrap>
        {errors.password && <Error>{errors.password.message}</Error>}

        <Button type="submit" disabled={!isValid}>
          Register Now
        </Button>
      </Form>
    </>
  );
};

export default Registration;
