// import { Modal } from '../../mainModal/MainModal';
// import { modalNames } from '../../../constant/constant';
import AddBoard from 'components/AddBoard/AddBoard';
import { CloseModalCross } from '../../mainModal/CloseModal';
import {
  ModalContainer,
  ModalTitle,
  ModalHeader,
  ModalBackdrop,
} from '../EditProfileModal/EditProfileModal.styled';
import { useDispatch } from 'react-redux';
import { useEffect } from 'react';

const AddBoardModal = () => {
  const dispatch = useDispatch();

  const closeModalOnBackdrop = e => {
    if (e.target === e.currentTarget) {
      dispatch(closeModal());
    }
  };
  useEffect(() => {
    const closeModalonEsc = e => {
      if (e.key === 'Escape') {
        dispatch(closeModal());
      }
    };

    return () => {
      window.removeEventListener('keydown', closeModalonEsc);
    };
  }, [dispatch]);
  return (
    // <Modal modalId={modalNames.ADD_BOARD}>

    <ModalBackdrop onClick={closeModalOnBackdrop}>
      <ModalContainer>
        <ModalHeader>
          <ModalTitle>New board</ModalTitle>
          <CloseModalCross />
        </ModalHeader>
        <AddBoard />
      </ModalContainer>
    </ModalBackdrop>
    // </Modal>
  );
};
export default AddBoardModal;
