import React, { useState } from 'react';
import { Container, Link, TextBox } from './Board.styled';
import AddBoardModal from 'components/Modals/AddBoardModal/AddBoardModal';
import { useHistory } from 'react-router-dom'; // Додаємо імпорт useHistory

const Board = () => {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [boardCreated, setBoardCreated] = useState(false); // Додаємо стейт для відстеження створення борда
  const history = useHistory(); // Створюємо об'єкт history для зміни шляху

  const openModal = () => {
    setIsModalOpen(true);
  };

  const closeModal = () => {
    setIsModalOpen(false);
    setBoardCreated(true); // Встановлюємо значення, що борд створено
  };

  // Функція для переходу на екранну сторінку після створення борда
  const redirectToScreensPage = () => {
    history.push('/screenspage');
  };

  return (
    <>
      <Container>
        <TextBox>
          Before starting your project, it is essential{' '}
          <Link onClick={openModal}>to create a board</Link> to visualize and
          track all the necessary tasks and milestones. This board serves as a
          powerful tool to organize the workflow and ensure effective
          collaboration among team members.
        </TextBox>
      </Container>
      {isModalOpen && <AddBoardModal onClose={closeModal} />}
      {boardCreated && redirectToScreensPage()}{' '}
      {/* Викликаємо функцію переходу на екранну сторінку */}
    </>
  );
};

export default Board;
