import styled from 'styled-components';

export const CloseModalCrossStyled = styled.button`
  background: none;
  border: none;
  cursor: pointer;
  float: inline-end;
`;
export const Div = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: rgba(0, 0, 0, 0.5); /* Прозорий фон */
  z-index: 1000;
  display: ${props => (props.isOpen ? 'flex' : 'none')};
  justify-content: center;
  align-items: center;
`;

export const ModalContainer = styled.div`
  border-radius: 8px;
  border: 1px solid ${props => props.theme.colors.priorityGreen};
  width: 335px;
  position: relative; /* Зміна позиції */
  z-index: 999;
  background-color: black;
  padding: 24px;
  box-shadow: 0px 4px 16px 0px rgba(22, 22, 22, 0.05);
  @media screen and (min-width: 768px) {
    width: 400px;
  }
`;
export const ModalHeader = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 24px;
`;

export const ModalTitle = styled.h2`
  color: ${props => props.theme.colors.whiteColor};
  font-size: 18px;
  font-style: normal;
  font-weight: 500;
  line-height: normal;
  letter-spacing: -0.36px;
`;
