import React, { useEffect, useState } from 'react';
import { Outlet, useNavigate, useParams } from 'react-router-dom';
import { Suspense } from 'react';
import {
  Modal,
  ModalNav,
  ModalNavItem,
  WelcomeWrapper,
  Wrap,
} from './Auth.styled';
import LogIn from 'components/Auth/LogInPg/LogInPg';
import Registration from 'components/Auth/RegistrationPg/RegistrationPg';

function AuthPage() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [registrationSuccessful, setRegistrationSuccessful] = useState(false);

  useEffect(() => {
    if (id === 'register') {
      if (registrationSuccessful) {
        navigate('/home');
      }
    }
  }, [id, navigate, registrationSuccessful]);

  return (
    <Wrap>
      <WelcomeWrapper>
        <Modal>
          <ModalNav>
            <ModalNavItem to="/auth/register">Registration</ModalNavItem>
            <ModalNavItem to="/auth/login">Log In</ModalNavItem>
          </ModalNav>

          {id === 'login' && <LogIn />}
          {id === 'register' && (
            <Registration
              setRegistrationSuccessful={setRegistrationSuccessful}
            />
          )}

          <Suspense>
            <Outlet />
          </Suspense>
        </Modal>
      </WelcomeWrapper>
    </Wrap>
  );
}

export default AuthPage;
