import { lazy, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { Route, Routes, Navigate } from 'react-router-dom';
import 'react-toastify/dist/ReactToastify.css';
import { refreshUser } from '../../redux/authorization/authReducer';
import { selectIsRefreshing, selectIsLoggedIn } from '../../redux/selectors';
import { ThemeProvider } from 'styled-components';
import { darkTheme } from '../../assets/theme/theme';
import Layout from 'components/Layout/Layout';
import Loader from 'components/Loader/Loader';
import PrivateRoute from 'components/Routes/PrivateRoute';
import PublicRoute from 'components/Routes/PublicRoute';
import WelcomePage from 'pages/WelcomePg/WelcomePage';
import HomePage from 'pages/Home/Home';
import ScreensPage from 'pages/ScreensPage/ScreensPage';
import AuthPage from 'components/Auth/Auth';
import LogIn from 'components/Auth/LogInPg/LogInPg';
import Registration from 'components/Auth/RegistrationPg/RegistrationPg';

const App = () => {
  const dispatch = useDispatch();
  const isRefreshing = useSelector(selectIsRefreshing);
  const isLoggedIn = useSelector(selectIsLoggedIn);

  useEffect(() => {
    dispatch(refreshUser());
  }, [dispatch]);

  return (
    <>
      <ThemeProvider theme={darkTheme}>
        {isRefreshing ? (
          <Loader />
        ) : (
          <Routes>
            <Route path="/" element={<Layout />}>
              {/* Render WelcomePage or HomePage based on isLoggedIn */}
              <Route
                index
                element={isLoggedIn ? <Navigate to="/home" /> : <WelcomePage />}
              />

              {/* Routes for authenticated users */}
              <Route
                path="/home"
                element={
                  <PrivateRoute redirectTo="/" component={<HomePage />} />
                }
              >
                <Route path=":boardName" element={<ScreensPage />} />
              </Route>

              {/* Routes for authentication */}
              <Route
                path="auth/:id"
                element={
                  <PublicRoute redirectTo="/" component={<AuthPage />} />
                }
              >
                <Route path="login" element={<LogIn />} />
                <Route path="registration" element={<Registration />} />
              </Route>
            </Route>
          </Routes>
        )}
      </ThemeProvider>
    </>
  );
};

export default App;
