import { registrationSchema } from '../../components/validation/schema';
import { useState } from 'react';
import { useForm } from 'react-hook-form';
import { useDispatch } from 'react-redux';
import { Eye } from 'assets/fonts/images/icons/Eye';
import { EyeSlash } from 'assets/fonts/images/icons/EyeSlash';
import { toast } from 'react-toastify';

import { Wrap, Input, Button, Form, Error } from './RegistrationPg.styled';

const Registration = () => {
  const [showPassword, setShowPassword] = useState(false);
  const dispatch = useDispatch();

  const initialValues = {
    name: '',
    email: '',
    password: '',
  };

  const {
    handleSubmit,
    register,
    formState: { errors, isValid },
    values,
    touched,
    handleChange,
    handleBlur,
  } = useForm({
    defaultValues: initialValues,
    resolver: registrationSchema,
  });

  const swapPassword = () => {
    setShowPassword(!showPassword);
  };

  const onSubmit = event => {
    event.preventDefault();
    const name = event.currentTarget.elements.name.value;
    const email = event.currentTarget.elements.email.value;
    const password = event.currentTarget.elements.password.value;

    // dispatch(apiRegisterUser({ email, password }))
    //   .then(() => {
    //     toast.success('Registration successful');
    //   })
    //   .catch(() => {
    //     toast.error('Failed to register. Please try again.');
    //   });
  };

  return (
    <>
      <Form onSubmit={handleSubmit(onSubmit)}>
        <Input
          type="name"
          {...register('name')}
          placeholder="Enter your name"
          id="name"
          name="name"
          value={values.name}
          onChange={handleChange}
          onBlur={handleBlur}
          $error={touched.name && errors.name}
        />
        {errors.name && <Error>{errors.name.message}</Error>}

        <Input
          type="email"
          {...register('email')}
          id="email"
          name="email"
          placeholder="Enter your email"
          value={values.email}
          onChange={handleChange}
          onBlur={handleBlur}
          $error={touched.email && errors.email}
        />
        {errors.email && <Error>{errors.email.message}</Error>}

        <Wrap>
          <Input
            type={showPassword ? 'text' : 'password'}
            {...register('password')}
            id="password"
            name="password"
            placeholder="Create a password"
            value={values.password}
            onChange={handleChange}
            onBlur={handleBlur}
            $error={touched.password && errors.password}
          />
          <button type="button" onClick={swapPassword}>
            {showPassword ? <Eye /> : <EyeSlash />}
          </button>
        </Wrap>
        {errors.password && <Error>{errors.password.message}</Error>}

        <Button type="submit" disabled={!isValid}>
          Register Now
        </Button>
      </Form>
    </>
  );
};

export default Registration;
