// import { Modal } from '../../mainModal/MainModal';
// import { modalNames } from '../../../constant/constant';
import AddBoard from 'components/AddBoard/AddBoard';
import { CloseModalCross } from '../../mainModal/CloseModal';
import {
  ModalContainer,
  ModalTitle,
  ModalHeader,
  ModalBackdrop,
} from '../EditProfileModal/EditProfileModal.styled';

import { useDispatch, useSelector } from 'react-redux';
import { useEffect } from 'react';
import { selectModalActiveModal } from '../../redux/modal/selectors';
import { closeModal } from '../../redux/modal/modalSlice';

const AddBoardModal = () => {
  const modalRoot = document.createElement('div');
  modalRoot.setAttribute('id', 'modal-root');
  document.body.appendChild(modalRoot);

  const dispatch = useDispatch();
  const activeModal = useSelector(selectModalActiveModal);
  const closeModalOnBackdrop = e => {
    if (e.target === e.currentTarget) {
      dispatch(closeModal());
    }
  };
  useEffect(() => {
    const closeModalonEsc = e => {
      if (e.key === 'Escape') {
        dispatch(closeModal());
      }
    };
    if (activeModal === modalId) {
      window.addEventListener('keydown', closeModalonEsc);
    }

    return () => {
      window.removeEventListener('keydown', closeModalonEsc);
    };
  });
  return (
    // <Modal modalId={modalNames.ADD_BOARD}>
    <>
      <ModalBackdrop onClick={closeModalOnBackdrop}>
        <ModalContainer>
          <ModalHeader>
            <ModalTitle>New board</ModalTitle>
            <CloseModalCross />
          </ModalHeader>
          <AddBoard />
        </ModalContainer>
      </ModalBackdrop>
    </>
    // </Modal>
  );
};
export default AddBoardModal;
