import AuthPage from 'components/Auth/Auth';
import LogIn from 'components/Auth/LogInPg/LogInPg';
import Registration from 'components/Auth/RegistrationPg/RegistrationPg';
import Layout from 'components/Layout/Layout';
import Loader from 'components/Loader/Loader';
import PrivateRoute from 'components/Routes/PrivateRoute';
import PublicRoute from 'components/Routes/PublicRoute';
import WelcomePage from 'pages/WelcomePg/WelcomePage';
import { useEffect } from 'react';
import { useDispatch } from 'react-redux';
import { Route, Routes } from 'react-router-dom';
import 'react-toastify/dist/ReactToastify.css';
import { refreshUser } from 'redux/authorization/authReducer';
export const App = () => {
  const dispatch = useDispatch();
  useEffect(() => {
    dispatch(refreshUser());
  }, [dispatch]);
  return (
    <>
      (<Loader />
      ):(
      <Routes>
        <Route path="/" element={<Layout />}>
          <Route
            index
            element={
              <PublicRoute redirectTo="/welcome" component={<WelcomePage />} />
            }
          />
          {/* <Route
            path="/home"
            element={<PrivateRoute redirectTo="/" component={<HomePage />} />}
          >
            {' '}
            <Route path=":boardName" element={<ScreenPage />} />
          </Route> */}
          <Route
            path="auth/:id"
            element={
              <PublicRoute redirectTo="/auth" component={<AuthPage />} />
            }
          >
            <Route path="login" element={<LogIn />} />
            <Route path="registration" element={<Registration />} />
          </Route>
        </Route>
      </Routes>
      )
    </>
  );
};
