import React, { useState, useEffect } from 'react';
import CustomModal from '../../mainModal/MainModal';
import AddBoard from 'components/AddBoard/AddBoard';
import { CloseModalCross } from '../../mainModal/CloseModal';
import {
  ModalContainer,
  ModalTitle,
  ModalHeader,
} from '../EditProfileModal/EditProfileModal.styled';

const AddBoardModal = ({ modalIsOpen, closeModal }) => {
  const [showModal, setShowModal] = useState(modalIsOpen);

  useEffect(() => {
    setShowModal(modalIsOpen);
  }, [modalIsOpen]);

  useEffect(() => {
    const handleKeyPress = event => {
      if (event.keyCode === 27) {
        // 27 is the key code for Esc
        closeModal();
      }
    };

    if (showModal) {
      document.addEventListener('keydown', handleKeyPress);
    }

    return () => {
      document.removeEventListener('keydown', handleKeyPress);
    };
  }, [showModal, closeModal]);

  // const handleClickBackdrop = event => {
  //   if (event.target === event.currentTarget) {
  //     closeModal();
  //   }
  // };

  const handleCloseModal = () => {
    closeModal();
  };

  return (
    <>
      {showModal && (
        <ModalContainer>
          <ModalHeader>
            <ModalTitle>New board</ModalTitle>
            <CloseModalCross onClick={handleCloseModal} />
          </ModalHeader>
          <AddBoard />
        </ModalContainer>
      )}
    </>
  );
};

export default AddBoardModal;
