import React, { useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import {
  deleteDashboard,
  getDashboardById,
} from '../../../redux/cards/cardsReducers';
import { closeMenuMode } from '../../../redux/menu/menuSlice';
import sprite from '../../../assets/fonts/images/icons/icons-sprite.svg';
import {
  Board,
  BoardIcon,
  BoardTitle,
  IconDel,
  IconEdit,
  IconsBlock,
  Item,
  StyledLink,
} from './BordItem.styled';
import { useNavigate } from 'react-router-dom';

const BoardItem = ({ board }) => {
  const dispatch = useDispatch();
  const currBoardId = useSelector(state => state.boards.currentBoardId);
  const [open, setOpen] = useState(false);
  const navigate = useNavigate();
  const handleNavigate = e => {
    if (e.dataset === 'icon') {
      return;
    }
    navigate(`/home/${board._id}`);
    dispatch(setCurrentBoardId(board._id));
    dispatch(closeSidebar());
    if (board._id !== currBoardId) {
      dispatch(clearColumns());
    }
  };
  const handleOpen = () => setOpen(true);

  const checkTextLength = text => {
    if (text) {
      const str = text.split('');

      if (str.length <= 10) {
        return str.join('');
      }
      return str.splice(0, 10).join('') + '...';
    }
    return '';
  };

  const handleDragStart = event => {
    event.preventDefault();
  };

  const handleContextMenu = event => {
    event.preventDefault();
  };

  return (
    <li
      id="boardBtn"
      onClick={handleNavigate}
      className={`${css.boardItem} ${
        board._id === currBoardId ? css.active : ''
      }`}
    >
      <div className={css.boardTitleBox}>
        {/* <svg width="18" height="18" className={css.boardIcon}>
          <use href={`${sprite}#icon-${board.icon}`} />
        </svg> */}
        <p>{board.name}</p>
      </div>
      <div className={css.boardButtonBox}>
        <button data-icon="icon" onClick={handleEditBoard}>
          {/* <svg
            data-icon="icon"
            className={css.iconButton}
            width="16"
            height="16"
          >
            <use data-icon="icon" href={`${sprite}#icon-pencil`} />
          </svg> */}
        </button>
        <button onClick={handleWarningBoard} data-icon="icon">
          {/* <svg
            data-icon="icon"
            className={css.iconButton}
            width="16"
            height="16"
          >
            <use data-icon="icon" href={`${sprite}#icon-trash`} />
          </svg> */}
        </button>
      </div>
    </li>
  );
};
