import React, { useState } from 'react';
import { useTheme } from '@mui/material';
import Header from '../Header/Header';
import { HomePageContainer, MainContainer } from './Home.styled';
import { ThemeProvider } from 'styled-components';
import Board from 'components/StartBoard/Board/Board';
import ScreensPage from '../../pages/Home/ScreensPage/ScreensPage';
import SuccessPage from 'path/to/SuccessPage'; // Імпортуємо компонент, який ви хочете показати після успішного додавання борда

const HomePage = () => {
  const theme = useTheme();
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [isBoardAdded, setIsBoardAdded] = useState(false); // Додайте стан, що відстежує успішність додавання борда

  const openModal = () => {
    setIsModalOpen(true);
  };

  const closeModal = () => {
    setIsModalOpen(false);
  };

  const handleBoardAdded = () => {
    setIsBoardAdded(true); // Встановлюємо стан, що був доданий новий борд
  };

  return (
    <ThemeProvider theme={theme}>
      <HomePageContainer>
        <MainContainer>
          <Header />
          <Board
            isModalOpen={isModalOpen}
            openModal={openModal}
            onBoardAdded={handleBoardAdded}
          />{' '}
          {/* Передаємо функцію, яка буде викликана після успішного додавання борда */}
          {isBoardAdded ? <SuccessPage /> : <ScreensPage />}{' '}
          {/* Використовуємо умовний рендеринг */}
        </MainContainer>
      </HomePageContainer>
    </ThemeProvider>
  );
};

export default HomePage;
