import styled from 'styled-components';

export const List = styled.ul`
  display: flex;
  flex-direction: column;
  gap: 10px;
  color: var(--mainTextColor);
  margin: 0;
  width: 100%;
  direction: ltr;
  @media screen and (min-width: 768px) {
    width: 260px;
  }

  ::-webkit-scrollbar {
    width: 0;
  }
`;
