import React from 'react';
import { Container, Link, TextBox } from './Board.styled';
import AddBoardModal from 'components/Modals/AddBoardModal/AddBoardModal';

const Board = () => {
  return (
    <Container>
      <TextBox>
        Before starting your project, it is essential{' '}
        <Link onClick={AddBoardModal()}>to create a board</Link> to visualize
        and track all the necessary tasks and milestones. This board serves as a
        powerful tool to organize the workflow and ensure effective
        collaboration among team members.
      </TextBox>
    </Container>
  );
};

export default Board;
