import React, { useState, useRef } from 'react';

import { ContentWrapper, Wrapper } from './MainBoard.styled';

import AddColumnModal from '../../Modals/AddColumModal/AddColumnModal';
import { useSelector } from 'react-redux';
import {
  selectColumns,
  selectColumnsLength,
  selectCurrentDashboard,
} from '../../../redux/selectors';
import ButtonPlus from 'components/ButtonPlus/ButtonPlus';

const MainBoard = () => {
  const columnLength = useSelector(selectColumnsLength);
  const currentDashboard = useSelector(selectCurrentDashboard);
  const columns = useSelector(selectColumns);

  const [open, setOpen] = useState(false);
  const [isDragging, setIsDragging] = useState(false);

  const scrollRef = useRef(null);
  const [startX, setStartX] = useState(0);

  const handleOpen = () => {
    setOpen(true);
    setIsDragging(false);
  };

  const handleCloseModal = () => {
    setOpen(false);
    setIsDragging(true);
  };

  const handleMouseDown = e => {
    if (e.button === 0) {
      const target = e.target.tagName.toLowerCase();
      if (target !== 'input' && target !== 'textarea') {
        setIsDragging(true);
        setStartX(e.pageX - scrollRef.current.offsetLeft);
      }
    }
  };

  const handleMouseMove = e => {
    if (!isDragging || open) return;
    const x = e.pageX - scrollRef.current.offsetLeft;
    const walk = (x - startX) * 0.05;
    scrollRef.current.scrollLeft = scrollRef.current.scrollLeft - walk;
  };

  const handleMouseUp = e => {
    if (e.button === 0) {
      setIsDragging(false);
    }
  };

  return (
    <Wrapper length={columnLength} ref={scrollRef}>
      <AddColumnModal modalIsOpen={open} closeModal={handleCloseModal} />
    </Wrapper>
  );
};

export default MainBoard;
