import styled from 'styled-components';

export const Button = styled.button`
  position: absolute;
  bottom: -20%;
  left: 38%;
  width: 100%;
  height: 45px;
  color: #161616;
  text-align: center;
  border: none;
  text-decoration: none;
  cursor: pointer;
  font-size: 14px;
  font-weight: 500;
  line-height: normal;
  letter-spacing: -0.28px;
  background-color: ${props =>
    props.approve === 'true' ? '#BEDBB0' : '#121212'};
  border-radius: 8px;
  padding: 14px;
  &:hover {
    transition: opacity 200ms linear;
    /* background-color: ${props =>
      props.approve === 'true' ? '#BEDBB0' : '#121212'}; */
  }
  @media screen and (min-width: 768px) {
    width: 336px;
  }
  @media screen and (min-width: 1440px) {
    width: 344px;
  }
`;
