import React from 'react';
import {
  Div,
  ModalContainer,
  ModalHeader,
  ModalTitle,
} from '../EditProfileModal/EditProfileModal.styled';
import { CloseModalCross } from '../../mainModal/CloseModal';
import AddBoard from 'components/AddBoard/AddBoard';

const AddBoardModal = ({ onClose }) => {
  const handleContainerClick = e => {
    // Зупиняємо розповсюдження події кліку, щоб вона не дійшла до батьківського елемента.
    e.stopPropagation();
  };

  return (
    <Div>
      <ModalContainer onClick={handleContainerClick}>
        <ModalHeader>
          <ModalTitle>New board</ModalTitle>
          <CloseModalCross onClose={onClose} />
        </ModalHeader>
        <AddBoard />
      </ModalContainer>
    </Div>
  );
};

export default AddBoardModal;
