import React, { useState, useRef } from 'react';

import { Wrapper } from './MainBoard.styled';

import AddColumnModal from '../../Modals/AddColumModal/AddColumnModal';
import { useSelector } from 'react-redux';
import {
  selectColumns,
  selectColumnsLength,
  selectCurrentDashboard,
} from '../../../redux/selectors';

const MainBoard = () => {
  const columnLength = useSelector(selectColumnsLength);

  const [open, setOpen] = useState(false);
  const [isDragging, setIsDragging] = useState(false);

  const scrollRef = useRef(null);
  const [startX, setStartX] = useState(0);

  const handleCloseModal = () => {
    setOpen(false);
  };

  return (
    <Wrapper length={columnLength} ref={scrollRef}>
      <AddColumnModal modalIsOpen={open} closeModal={handleCloseModal} />
    </Wrapper>
  );
};

export default MainBoard;
