import MainModal from '../../mainModal/MainModal';
import NewBoardForm from 'components/forms/newBoardForm/NewBoardForm';
import { useState } from 'react';
import { Container, Link, TextBox } from './Board.styled';
import { useTheme } from '@mui/material';
import { useAddBoardMutation } from 'redux/boards/boardsApi';

const Board = () => {
  const [openModal, setOpenModal] = useState(false);

  const [addBoard] = useAddBoardMutation();

  const closeModal = () => {
    setOpenModal(false);
  };

  const handleSubmit = data => {
    addBoard({ data });
    closeModal();
  };

  const theme = useTheme();
  return (
    <Container>
      <TextBox theme={theme}>
        Before starting your project, it is essential
        <Link theme={theme} onClick={() => setOpenModal(true)}>
          to create a board
        </Link>
        to visualize and track all the necessary tasks and milestones. This
        board serves as a powerful tool to organize the workflow and ensure
        effective collaboration among team members.
      </TextBox>
      <MainModal modalIsOpen={openModal} closeModal={closeModal}>
        <NewBoardForm
          formTitle={'New board'}
          btnText={'Create'}
          handleSubmit={handleSubmit}
          closeModal={closeModal}
        ></NewBoardForm>
      </MainModal>
    </Container>
  );
};

export default Board;
