import React from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { Wrapper } from './ScreensPage.styled';
import { selectIsMenuOpen } from '../../redux/selectors';
import { closeMenuMode } from '../../redux/menu/menu';

import HeaderDashboard from './HeaderDashboard/HeaderDashboard';
import MainBoard from './MainBoard/MainBoard';
import Board from './Board'; // Import the Board component here

const ScreensPage = () => {
  const dispatch = useDispatch();

  const menuMode = useSelector(selectIsMenuOpen);
  const currentBg = useSelector(state => state?.dashboards?.currentBg);
  const currentName = useSelector(state => state?.dashboards?.currentName);

  const handleScreenClick = () => {
    if (menuMode) {
      dispatch(closeMenuMode());
    }
  };

  return (
    <Wrapper onClick={handleScreenClick} bgcUrl={currentBg} isOpen={menuMode}>
      {currentName ? (
        <>
          <HeaderDashboard>{currentName}</HeaderDashboard>
          <MainBoard />
        </>
      ) : (
        <Board /> // Render the Board component when currentName is falsy
      )}
    </Wrapper>
  );
};

export default ScreensPage;
