    // Зупиняємо розповсюдження події кліку, щоб вона не дійшла до батьківського елемента.
    e.stopPropagation();
    onClose();
  };

  return (
    <CloseModalCrossStyled type="button" onClick={closeModal}>
      <Close />
    </CloseModalCrossStyled>
  );
};
