import { Button, IconWrapper, Icon, Wrap } from './ButtonPlus.styled';
import sprite from '../../assets/fonts/images/icons/icons-sprite.svg';

const ButtonPlus = ({ text, approve, onSubmit }) => {
  const handleClick = () => {
    onSubmit(); // Виклик onSubmit, який містить handleSubmit
  };

  return (
    <Button type="button" $approve={approve} onClick={handleClick}>
      <Wrap>
        <IconWrapper>
          <Icon>
            <use href={sprite + '#icon-plus'} />
          </Icon>
        </IconWrapper>
        {text}
      </Wrap>
    </Button>
  );
};
export default ButtonPlus;
