import { registrationSchema } from '../../components/validation/schema';
import { useState } from 'react';
import { useForm } from 'react-hook-form';
import { useDispatch } from 'react-redux';
import { Eye } from 'assets/fonts/images/icons/Eye';
import { EyeSlash } from 'assets/fonts/images/icons/EyeSlash';
import { registration } from '../../redux/authorization/authReducer';
import { toast } from 'react-toastify';

import { Wrap, Input, Button, Form, Error } from './RegistrationPg.styled';

const Registration = () => {
  const [showPassword, setShowPassword] = useState(false);
  const dispatch = useDispatch();

  const {
    register,

    isValid,
    formState: { errors },
    reset,
  } = useForm();

  const swapPassword = () => {
    setShowPassword(!showPassword);
  };

  const onSubmit = data => {
    const { name, email, password } = data;
    console.log(data);
    dispatch(
      registration({
        name,
        email,
        password,
      })
    );
    reset(); // Clear form fields
  };

  return (
    <>
      <Form onSubmit={onSubmit}>
        <Input
          type="name"
          placeholder="Enter your name"
          id="name"
          name="name"
          autoComplete="off"
          {...register('name', { required: 'Name is required' })}
        />
        {errors.name && <div>{errors.name.message}</div>}

        <Input
          type="email"
          id="email"
          name="email"
          placeholder="Enter your email"
          autoComplete="off"
          {...registration('email', { required: 'Email is required' })}
        />
        {errors.email && <div>{errors.email.message}</div>}

        <Wrap>
          <Input
            type="password"
            id="password"
            name="password"
            placeholder="Create a password"
            autoComplete="off"
            {...registration('password', { required: 'Password is required' })}
          />

          <button type="button" onClick={swapPassword}>
            {showPassword ? <Eye /> : <EyeSlash />}
          </button>
        </Wrap>
        {errors.password && <div>{errors.password.message}</div>}

        <Button type="submit" disabled={!isValid}>
          Register Now
        </Button>
      </Form>
    </>
  );
};

export default Registration;
