import { registrationSchema } from '../../components/validation/schema';
import { useState } from 'react';
import { useForm } from 'react-hook-form';
import { useDispatch } from 'react-redux';
import { Eye } from 'assets/fonts/images/icons/Eye';
import { EyeSlash } from 'assets/fonts/images/icons/EyeSlash';
import { registration } from '../../redux/authorization/authReducer';
import { toast } from 'react-toastify';

import { Wrap, Input, Button, Form, Error } from './RegistrationPg.styled';

const Registration = () => {
  const [showPassword, setShowPassword] = useState(false);
  const dispatch = useDispatch();

  const swapPassword = () => {
    setShowPassword(!showPassword);
  };

  const onSubmit = event => {
    event.preventDefault();
    const name = event.currentTarget.elements.name.value;
    const email = event.currentTarget.elements.email.value;
    const password = event.currentTarget.elements.password.value;

    const formData = {
      name,
      email,
      password,
    };

    dispatch(registration(formData))
      .unwrap()
      .then(() => {
        event.currentTarget.elements.name.value;
        event.currentTarget.elements.email.value = '';
        event.currentTarget.elements.password.value = '';
      })
      .catch(() => toast.error('Please, write a correct email or password!'));
  };
  const { values, touched, errors, handleChange, handleBlur } = useForm({
    initialValues: {
      name: '',
      email: '',
      password: '',
    },
    validationSchema: signInSchema,
    onSubmit,
  });

  return (
    <>
      <Form onSubmit={onSubmit}>
        <Input
          type="name"
          placeholder="Enter your name"
          id="name"
          name="name"
          autoComplete="off"
          value={values.name}
          onChange={handleChange}
          $error={touched.name && errors.name}
        />
        {touched.name && errors.name && <Error>{errors.name}</Error>}

        <Input
          type="email"
          id="email"
          name="email"
          placeholder="Enter your email"
          autoComplete="off"
          value={values.email}
          onChange={handleChange}
          onBlur={handleBlur}
          $error={touched.email && errors.email}
        />
        {touched.email && errors.email && <Error>{errors.email}</Error>}

        <Wrap>
          <Input
            type="password"
            id="password"
            name="password"
            placeholder="Create a password"
            autoComplete="off"
            value={values.password}
            onChange={handleChange}
            onBlur={handleBlur}
            $error={touched.password && errors.password}
          />
          {touched.password && errors.password && (
            <Error>{errors.password}</Error>
          )}

          <button type="button" onClick={swapPassword}>
            {showPassword ? <Eye /> : <EyeSlash />}
          </button>
        </Wrap>

        <Button type="submit" disabled={!isValid}>
          Register Now
        </Button>
      </Form>
    </>
  );
};

export default Registration;
