import React, { useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { useForm } from 'react-hook-form'; // Import useForm
import { yupResolver } from '@hookform/resolvers/yup'; // Import yupResolver
import { selectUser, selectUserTheme } from '../../redux/modal/selectors'; // Import selectors
import { updateUser } from '../../redux/authorization/authReducer'; // Import updateUser action
import dark from '../../assets/fonts/images/userLogo/dark.svg';
import light from '../../assets/fonts/images/userLogo/light.svg';
import violet from '../../assets/fonts/images/userLogo/violet.svg';
import { registrationSchema } from 'components/validation/schema';
import {
  Wrapper,
  UserButton,
  Image,
  Icon,
  HiddenInput,
} from './EditProfile.styled.component';
import {
  Form,
  Button,
  Wrap,
  Input,
  Error,
} from 'components/Auth/RegistrationPg/RegistrationPg.styled';

const EditProfile = () => {
  const dispatch = useDispatch();
  const { name, email, avatarURL } = useSelector(selectUser);
  const activeTheme = useSelector(selectUserTheme);
  const [fileImage, setFileImage] = useState(null);
  const [currentImageUrl, setCurrentImageUrl] = useState(avatarURL);

  const {
    register,
    handleSubmit,
    formState: { errors, touched = {} },
  } = useForm({
    mode: 'onBlur', // Validate on blur
    defaultValues: {
      image: currentImageUrl,
      name: '',
      email: '',
      password: '',
    },
    resolver: yupResolver(registrationSchema),
  });

  const handleImageUpload = event => {
    const file = event.target.files[0];

    setFileImage(file);

    const reader = new FileReader();

    reader.onload = upload => {
      setCurrentImageUrl(upload.target.result);
    };

    if (file) {
      reader.readAsDataURL(file);
    }
  };

  const setDefaultAvatar = () => {
    if (activeTheme === 'dark') {
      return dark;
    } else if (activeTheme === 'light') {
      return light;
    } else if (activeTheme === 'violet') {
      return violet;
    }
  };

  const changeImage = () => {
    if (currentImageUrl === '') {
      return avatarURL;
    }

    return currentImageUrl;
  };

  const onSubmit = values => {
    const { name, email, password } = values;

    const formData = new FormData();

    formData.append('name', name);
    formData.append('email', email);
    formData.append('password', password);

    if (fileImage) {
      formData.append('avatarURL', fileImage);
    }

    dispatch(updateUser(formData));
  };

  return (
    <>
      <Wrapper url={!currentImageUrl && setDefaultAvatar()}>
        {currentImageUrl && <Image src={changeImage()} alt="user" />}
        <UserButton
          onClick={() => document.querySelector('.input-field').click()}
        >
          <Icon>
            <use href={sprite + '#icon-plus'} />{' '}
            {/* Import and use sprite properly */}
          </Icon>

          <HiddenInput
            className="input-field"
            type="file"
            accept="image/*"
            name="imageURL"
            onChange={handleImageUpload}
          />
        </UserButton>
      </Wrapper>
      <Form onSubmit={handleSubmit(onSubmit)}>
        <Input
          type="text"
          placeholder="Enter your name"
          id="name"
          {...register('name')}
          error={touched.name && errors.name?.message}
        />
        {errors.name && <Error>{errors.name.message}</Error>}

        <Input
          type="email"
          id="email"
          placeholder="Enter your email"
          {...register('email')}
          error={touched.email && errors.email?.message}
        />
        {errors.email && <Error>{errors.email.message}</Error>}

        <Wrap>
          <Input
            type="password"
            id="password"
            placeholder="Create a password"
            {...register('password')}
            error={touched.password && errors.password?.message}
          />
          {errors.password && <Error>{errors.password.message}</Error>}
        </Wrap>

        <Button type="submit">Register Now</Button>
      </Form>
    </>
  );
};

export default EditProfile;
