import Burger from 'assets/fonts/images/icons/Burger';
import EditProfileModal from 'components/Modals/EditProfileModal/EditProfileModal';
import {
  HeaderSection,
  BurgerBtn,
  HeaderUserWrap,
  UserText,
  UserButton,
  UserImage,
} from './Header.styled';
import { selectUserData } from '../../redux/selectors';
import React, { useState } from 'react';
import avatarLight from '../../assets/fonts/images/userLogo/userLight.jpg';
import avatarDark from '../../assets/fonts/images/userLogo/userDark.jpg';
import { useSelector } from 'react-redux';

const Header = ({ openMenu }) => {
  const user = useSelector(selectUserData);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const theme = user.theme;

  const currentUserData = {
    avatar: user.avatar,
    login: user.name,
    email: user.email,
  };

  const openModal = () => {
    setIsModalOpen(true);
  };

  const closeModal = () => {
    setIsModalOpen(false);
  };

  return (
    <HeaderSection>
      <BurgerBtn type="button">
        <Burger width={24} height={24} fillColor={'#161616'} />
      </BurgerBtn>
      <UserText
        variant="body2"
        sx={{
          color: 'text.primary',
        }}
      >
        {user.name}
      </UserText>
      <UserButton onClick={openModal} aria-label="open drawer">
        {theme === 'dark' ? (
          <UserImage src={currentUserData.avatar || avatarDark} alt="Avatar" />
        ) : (
          <UserImage src={currentUserData.avatar || avatarLight} alt="Avatar" />
        )}
      </UserButton>
      <HeaderUserWrap>
        {/* Render the modal only when isModalOpen is true */}
        {isModalOpen && <EditProfileModal onClose={closeModal} />}
      </HeaderUserWrap>
    </HeaderSection>
  );
};

export default Header;
