import { Container, Link, TextBox } from './Board.styled';
import AddBoardModal from 'components/Modals/AddBoardModal/AddBoardModal';
import { Outlet } from 'react-router-dom'; // імпортуються виходи (outlets)
import React, { useState } from 'react';
import { useSelector } from 'react-redux';
import { Suspense, useEffect } from 'react';
const Board = () => {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [boards, setBoards] = useState([]);

  const openModal = () => {
    setIsModalOpen(true);
  };

  const closeModal = () => {
    setIsModalOpen(false);
  };

  const handleBoardCreated = newBoard => {
    setBoards([...boards, newBoard]); // додаємо новий борд до списку бордів
    closeModal(); // закриваємо модальне вікно після створення борда
  };

  return (
    <>
      <Container>
        <TextBox>
          Before starting your project, it is essential{' '}
          <Link onClick={openModal}>to create a board</Link> to visualize and
          track all the necessary tasks and milestones. This board serves as a
          powerful tool to organize the workflow and ensure effective
          collaboration among team members.
        </TextBox>
      </Container>
      {isModalOpen && (
        <AddBoardModal
          onClose={closeModal}
          onBoardCreated={handleBoardCreated}
        />
      )}{' '}
      {/* Передається колбек handleBoardCreated */}
      <Suspense fallback={<div>Loading....</div>}>
        <Outlet boards={boards} />{' '}
        {/* Передається список бордів у компонент Outlet */}
      </Suspense>
    </>
  );
};

export default Board;
