import React from 'react';
import AddBoard from 'components/AddBoard/AddBoard';
import { CloseModalCross } from '../../mainModal/CloseModal';
import {
  ModalContainer,
  ModalTitle,
  ModalHeader,
  Div,
} from '../EditProfileModal/EditProfileModal.styled';

const AddBoardModal = () => {
  const handleClickBackdrop = () => {
    closeModal(); // Викликайте як функцію, оскільки ви передаєте її як пропс
  };

  const handleCloseModal = e => {
    e.stopPropagation(); // Зупиняємо подію клікання на хрестик для уникнення закриття модального вікна
    closeModal(); // Викликайте як функцію
  };

  return (
    <Div onClick={handleClickBackdrop}>
      <ModalContainer>
        <ModalHeader>
          <ModalTitle>New board</ModalTitle>
          <CloseModalCross onClick={handleCloseModal} />{' '}
          {/* Викликайте як функцію handleCloseModal */}
        </ModalHeader>
        <AddBoard />
      </ModalContainer>
    </Div>
  );
};

export default AddBoardModal;
