import ButtonPlus from 'components/ButtonPlus/ButtonPlus';
import { editColumnSchema } from 'components/validation/schema';
import {
  Form,
  Input,
  Error,
} from 'components/Auth/RegistrationPg/RegistrationPg.styled';
import { toast } from 'react-toastify';
import { useForm } from 'react-hook-form';
import { yupResolver } from '@hookform/resolvers/yup';

const EditColumn = () => {
  const {
    register,
    handleSubmit,
    formState: { errors, touched = {} },
  } = useForm({
    mode: 'onBlur',
    defaultValues: {
      title: '',
    },
    resolver: yupResolver(editColumnSchema),
  });

  const onSubmit = values => {
    console.log(values);
    if (!values.message) {
      toast.error('Title must be at least 3 characters long');
      return;
    }
    onSubmit(values);
  };
  return (
    <Form onSubmit={handleSubmit(onSubmit)}>
      <Input
        type="text"
        id="text"
        placeholder="Title"
        {...register('text')}
        error={touched.text && errors.text.message}
      />
      {errors.text && <Error>{errors.text.message}</Error>}

      <ButtonPlus approve={'true'} type="button" text="Add" />
    </Form>
  );
};
export default EditColumn;
