import { registrationSchema } from '../../components/validation/schema';
import { useState } from 'react';
import { useForm } from 'react-hook-form';
import { useDispatch } from 'react-redux';
import { Eye } from 'assets/fonts/images/icons/Eye';
import { EyeSlash } from 'assets/fonts/images/icons/EyeSlash';
import { registration } from '../../redux/authorization/authReducer';
import { toast } from 'react-toastify';

import { Wrap, Input, Button, Form, Error } from './RegistrationPg.styled';

const Registration = () => {
  const [showPassword, setShowPassword] = useState(false);
  const dispatch = useDispatch();

  const {
    formState: { errors, touched, isValid },
    register,
  } = useForm({
    initialValues: {
      name: '',
      email: '',
      password: '',
    },
    registrationSchema,
  });

  const swapPassword = () => {
    setShowPassword(!showPassword);
  };

  const onSubmit = data => {
    const { name, email, password } = data;

    dispatch(registration({ name, email, password }))
      .unwrap()
      .then(() => {
        toast.success('Login successful!');
        // Clear input fields
        register('email').value = '';
        register('password').value = '';
      })
      .catch(() => toast.error('Please, write a correct email or password!'));
  };

  return (
    <>
      <Form onSubmit={onSubmit}>
        <Input
          type="text"
          placeholder="Enter your name"
          id="name"
          name="name"
          $error={touched && touched.name && errors.name}
        />
        {errors.name && <Error>{errors.name.message}</Error>}

        <Input
          type="email"
          id="email"
          name="email"
          placeholder="Enter your email"
          $error={touched && touched.email && errors.email}
        />
        {errors.email && <Error>{errors.email.message}</Error>}

        <Wrap>
          <Input
            type="password"
            id="password"
            name="password"
            placeholder="Create a password"
            $error={touched && touched.password && errors.password}
          />
          <button type="button" onClick={swapPassword}>
            {showPassword ? <Eye /> : <EyeSlash />}
          </button>
        </Wrap>
        {errors.password && <Error>{errors.password.message}</Error>}

        <Button type="submit" disabled={!isValid}>
          Register Now
        </Button>
      </Form>
    </>
  );
};

export default Registration;
