import React, { useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import {
  deleteDashboard,
  getDashboardById,
} from '../../../redux/cards/cardsReducers';
import { closeMenuMode } from '../../../redux/menu/menuSlice';
import sprite from '../../../assets/fonts/images/icons/icons-sprite.svg';
import {
  Board,
  BoardIcon,
  BoardTitle,
  IconDel,
  IconEdit,
  IconsBlock,
  Item,
  StyledLink,
} from './BordItem.styled';
import { useNavigate } from 'react-router-dom';

const BoardItem = ({ board }) => {
  const dispatch = useDispatch();
  const currBoardId = useSelector(state => state.boards.currentBoardId);
  const [open, setOpen] = useState(false);
  const navigate = useNavigate();
  const handleNavigate = e => {
    if (e.dataset === 'icon') {
      return;
    }
    navigate(`/home/${board._id}`);
    dispatch(setCurrentBoardId(board._id));
    dispatch(closeSidebar());
    if (board._id !== currBoardId) {
      dispatch(clearColumns());
    }
  };
  const handleOpen = () => setOpen(true);

  const checkTextLength = text => {
    if (text) {
      const str = text.split('');

      if (str.length <= 10) {
        return str.join('');
      }
      return str.splice(0, 10).join('') + '...';
    }
    return '';
  };

  const handleDragStart = event => {
    event.preventDefault();
  };

  const handleContextMenu = event => {
    event.preventDefault();
  };

  return (
    <>
      <Item
        draggable={false}
        onDragStart={handleDragStart}
        onContextMenu={handleContextMenu}
      >
        <StyledLink to={board.path}>
          <Board>
            <BoardIcon>
              <use href={sprite + board.icon} />
            </BoardIcon>

            <BoardTitle
              onClick={() => {
                dispatch(closeMenuMode());
                dispatch(getDashboardById(board._id));
              }}
            >
              {checkTextLength(board.name)}
            </BoardTitle>
          </Board>
          <IconsBlock>
            {open && (
              <IconEdit aria-label="edit icon" onClick={handleOpen}>
                <use href={sprite + `#icon-pencil`} />
              </IconEdit>
            )}

            <IconDel
              aria-label="delete icon"
              onClick={() => {
                dispatch(deleteDashboard(board._id));
              }}
            >
              <use href={sprite + `#icon-trash`} />
            </IconDel>
          </IconsBlock>
        </StyledLink>
      </Item>

      {/* <EditBoardModal onClose={handleClose} /> */}
    </>
  );
};

export default BoardItem;
