import { registrationSchema } from '../../components/validation/schema';
import { useState } from 'react';
import { useForm } from 'react-hook-form';
import { useDispatch } from 'react-redux';
import { Eye } from 'assets/fonts/images/icons/Eye';
import { EyeSlash } from 'assets/fonts/images/icons/EyeSlash';
import { registration } from '../../redux/authorization/authReducer';
import { toast } from 'react-toastify';

import { Wrap, Input, Button, Form, Error } from './RegistrationPg.styled';

function Registration() {
  const [showPassword, setShowPassword] = useState(false);
  const dispatch = useDispatch();

  const swapPassword = () => {
    setShowPassword(!showPassword);
  };

  const {
    handleSubmit,
    formState: { errors, touched, isValid },
    register,
  } = useForm({
    initialValues: {
      name: '',
      email: '',
      password: '',
    },
    validationSchema: registrationSchema,
  });

  const onSubmit = data => {
    dispatch(registration(data))
      .unwrap()
      .then(() => {
        // Clear input fields after successful registration
        register('name').value = '';
        register('email').value = '';
        register('password').value = '';
        // Optionally, you can show a success message
        toast.success('Registration successful!');
      })
      .catch(() => {
        // Optionally, you can show an error message
        toast.error('Please write a correct email or password!');
      });
  };

  return (
    <>
      <Form onSubmit={handleSubmit(onSubmit)}>
        <Input
          type="name"
          placeholder="Enter your name"
          id="name"
          name="name"
          autoComplete="off"
          {...register('name')}
          $error={touched.name && errors.name}
        />
        {touched.name && errors.name && <Error>{errors.name.message}</Error>}

        <Input
          type="email"
          id="email"
          name="email"
          placeholder="Enter your email"
          autoComplete="off"
          {...register('email')}
          $error={touched.email && errors.email}
        />
        {touched.email && errors.email && <Error>{errors.email.message}</Error>}

        <Wrap>
          <Input
            type="password"
            id="password"
            name="password"
            placeholder="Create a password"
            autoComplete="off"
            {...register('password')}
            $error={touched.password && errors.password}
          />
          <button type="button" onClick={swapPassword}>
            {showPassword ? <Eye /> : <EyeSlash />}
          </button>
          {touched.password && errors.password && (
            <Error>{errors.password.message}</Error>
          )}
        </Wrap>

        <Button type="submit" disabled={!isValid}>
          Register Now
        </Button>
      </Form>
    </>
  );
}

export default Registration;
