import Burger from 'assets/fonts/images/icons/Burger';
import EditProfileModal from 'components/Modals/EditProfileModal/EditProfileModal';
import {
  HeaderSection,
  BurgerBtn,
  HeaderUserWrap,
  UserText,
  UserButton,
  UserImage,
} from './Header.styled';
import { selectUserData } from '../../redux/selectors';
import React, { useState } from 'react';
import avatarLight from '../../assets/fonts/images/userLogo/userLight.jpg';
import avatarDark from '../../assets/fonts/images/userLogo/userDark.jpg';
import { useSelector } from 'react-redux';

const Header = () => {
  const user = useSelector(selectUserData);
  const [isModalOpen, setIsModalOpen] = useState(false);

  const dispatch = useDispatch();
  const avatarURL = useSelector(state => state.avatarURL);
  const theme = useSelector(state => state.auth.theme);
  const [fileImage, setFileImage] = useState(null);
  const [selectedAvatar, setSelectedAvatar] = useState(avatarURL);

  const handleFileSelect = event => {
    const file = event.target.files[0];

    if (file.size > 50 * 1024) {
      toast.error('The file size must not exceed 50 KB');
      return;
    }

    if (file) {
      setFileImage(file);
      const reader = new FileReader();

      reader.onload = () => {
        setSelectedAvatar(reader.result);
      };

      reader.readAsDataURL(file);
    }
  };

  const openModal = () => {
    setIsModalOpen(true);
  };

  const closeModal = () => {
    setIsModalOpen(false);
  };

  return (
    <HeaderSection>
      <BurgerBtn type="button">
        <Burger width={24} height={24} fillColor={'#161616'} />
      </BurgerBtn>
      <UserText
        variant="body2"
        sx={{
          color: 'text.primary',
        }}
      >
        {user.name}
      </UserText>
      <UserButton onClick={openModal} aria-label="open drawer">
        <Avatar
          src={selectedAvatar || (theme === 'dark' ? userLight : userDark)}
          alt="Avatar"
        />
      </UserButton>
      <HeaderUserWrap>
        {/* Render the modal only when isModalOpen is true */}
        <EditProfileModal onClose={closeModal} />
      </HeaderUserWrap>
    </HeaderSection>
  );
};

export default Header;
