import { registrationSchema } from '../../components/validation/schema';
import { useForm } from 'react-hook-form';
import { useDispatch } from 'react-redux';
import { registration } from '../../redux/authorization/authReducer';
import { toast } from 'react-toastify';

import { Wrap, Input, Button, Form, Error } from './RegistrationPg.styled';

function Registration() {
  const dispatch = useDispatch();
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    password: '',
  });
  const handleChange = e => {
    const { name, value } = e.target;
    setFormData(prevState => ({
      ...prevState,
      [name]: value,
    }));
  };

  const {
    handleSubmit,
    formState: { errors, isValid },
    register,
  } = useForm({
    initialValues: {
      name: '',
      email: '',
      password: '',
    },
    validationSchema: registrationSchema,
  });

  const onSubmit = event => {
    const name = event.target.elements.name.value;
    const email = event.target.elements.email.value;
    const password = event.target.elements.password.value;

    const formData = {
      name,
      email,
      password,
    };

    dispatch(registration(formData))
      .unwrap()
      .then(() => {
        event.target.elements.name.value = '';
        event.target.elements.email.value = '';
        event.target.elements.password.value = '';

        toast.success('Registration successful!');
      })
      .catch(() => {
        toast.error('Please write a correct email or password!');
      });
  };

  return (
    <>
      <Form onSubmit={handleSubmit(onSubmit)}>
        <Input
          type="name"
          placeholder="Enter your name"
          id="name"
          name="name"
          autoComplete="off"
          {...register('name')}
          $error={errors.name}
        />
        {errors.name && <Error>{errors.name.message}</Error>}

        <Input
          type="email"
          id="email"
          name="email"
          placeholder="Enter your email"
          autoComplete="off"
          {...register('email')}
          $error={errors.email}
        />
        {errors.email && <Error>{errors.email.message}</Error>}

        <Wrap>
          <Input
            type="password"
            id="password"
            name="password"
            placeholder="Create a password"
            autoComplete="off"
            {...register('password')}
            $error={errors.password}
          />

          {errors.password && <Error>{errors.password.message}</Error>}
        </Wrap>

        <Button type="submit" disabled={!isValid}>
          Register Now
        </Button>
      </Form>
    </>
  );
}

export default Registration;
