import styled from 'styled-components';
import { NavLink } from 'react-router-dom';
import { theme } from './theme';

// Styled components
const WelcomeWrapper = styled.div`
  height: 100vh;
  background: linear-gradient(
    180deg,
    rgba(196, 196, 196, 0) 25%,
    ${props => props.theme.colors.primaryGreen} 92.19%
  );
  display: flex;
  justify-content: center;
  align-items: center;
`;

const Modal = styled.div`
  border: 1px solid ${props => props.theme.colors.primaryBlack};
  width: calc(100% - 84px);
  padding: 24px;
  background: ${props => props.theme.colors.bgForm};
  border-radius: ${props => props.theme.borderRadius.main};

  @media (min-width: 375px) {
    width: 335px;
  }

  @media (min-width: 768px) {
    border: 1px solid ${props => props.theme.colors.primaryBlack};
    padding: 40px;
    width: 424px;
  }
`;

const ModalNav = styled.div`
  display: flex;
  gap: 14px;
  margin-bottom: 40px;
`;

const ModalNavItem = styled(NavLink)`
  font-family: 'Poppins';
  font-weight: ${props => props.theme.fontWeights.medium};
  font-size: ${props => props.theme.fontSizes.subtitle};
  line-height: ${props => props.theme.lineHeights.main};
  letter-spacing: ${props => props.theme.letterSpacing.small};
  text-decoration: none;
  color: ${props => props.theme.colors.secondaryGray};
  background: none;
  border: none;
  transition: color ${props => props.theme.transition.normal};

  &:hover,
  &.active {
    color: ${props => props.theme.colors.primaryWhite};
  }
`;
