import { useParams } from 'react-router-dom';
import { selectBoards } from '../../../redux/selectors';
import { useSelector } from 'react-redux';
import { useEffect } from 'react';
import { useDispatch } from 'react-redux';
import { getDashboardById } from '../../../redux/cards/cardsReducers';
import HeaderDashboard from 'components/Boards/HeaderDashboard';
import MainDashboard from 'components/Boards/MainDashboard';

const ScreensPage = () => {
  const { boardId } = useParams();
  const boards = useSelector(selectBoards);
  const currentBoard = boards.find(board => board._id === boardId);
  const bgImg = require(`../../../assets/fonts/images/backs-small/1x/cappadocia-baloons.jpg${
    currentBoard?.background ?? '2'
  }@2x.jpg`);
  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(getDashboardById(boardId));
  });

  return (
    <div
      style={
        currentBoard?.background !== '1'
          ? {
              backgroundImage: `linear-gradient(to bottom, rgba(0, 0, 0, 0.6), rgba(0, 0, 0, 0.6)), url(${bgImg})`,
              backgroundPosition: 'center',
              backgroundSize: 'cover',
            }
          : {}
      }
    >
      <HeaderDashboard board={currentBoard} />
      <MainDashboard board={currentBoard} />
    </div>
  );
};

export default ScreensPage;
