import { needHelpSchema } from 'components/validation/schema';
import {
  Form,
  Button,
  Wrap,
  Input,
  Error,
} from 'components/Auth/RegistrationPg/RegistrationPg.styled';
import { toast } from 'react-toastify';
import { useDispatch } from 'react-redux';

const NeedHelp = () => {
  const dispatch = useDispatch();
  const {
    register,
    handleSubmit,
    formState: { errors, touched = {} },
  } = useForm({
    mode: 'onBlur',
    defaultValues: {
      email: '',
      message: '',
    },
    resolver: yupResolver(needHelpSchema),
  });
  const onSubmit = (values, { resetForm }) => {
    const { email, message } = values;
    const messageTrim = message.trim();

    if (!messageTrim) {
      toast.error('Sorry, but you need to describe your problem!');
      return;
    }
    const formData = { email, message };

    dispatch(needHelp(formData));
    resetForm();
  };
  return (
    <Form onSubmit={handleSubmit(onSubmit)}>
      <Input
        type="email"
        id="email"
        placeholder="Email address"
        {...register('email')}
        error={touched.email && errors.email?.message}
      />
      {errors.email && <Error>{errors.email.message}</Error>}

      <Wrap>
        <Text
          type="text"
          id="message"
          placeholder="Comment"
          {...register('comment')}
          error={touched.comment && errors.comment.message}
        />
        {errors.comment && <Error>{errors.comment.message}</Error>}
      </Wrap>

      <Button type="submit">Send</Button>
    </Form>
  );
};
export default NeedHelp;
