      icon,
    };

    const alreadyExists = dashboards.findIndex(item => {
      const name = item.name.toLowerCase();
      const newName = dashboard.name.toLowerCase();
      return name === newName;
    });

    if (alreadyExists >= 0) {
      return `${dashboard.name} is already added to contact list`;
    } else {
      // dispatch(addDashboard(dashboard));
    }
    reset();
  };
  const iconsArray = [
    '#icon-Project',
    '#icon-star',
    '#icon-loading',
    '#icon-puzzle-piece',
    '#icon-container',
    '#icon-lightning',
    '#icon-colors',
    '#icon-hexagon',
  ];
  return (
    <Form onSubmit={handleSubmit(onSubmit)}>
      <Input
        type="text"
        id="text"
        placeholder="To do"
        {...register('text')}
        error={touched.text && errors.text && errors.text.message}
      />
      {errors.text && <Error>{errors.text.message}</Error>}

      <ButtonPlus type="submit" approve="true" text="Create" />
    </Form>
  );
};
export default AddBoard;
