import ButtonPlus from 'components/ButtonPlus/ButtonPlus';
import { editColumnSchema } from 'components/validation/schema';
import {
  Input,
  Error,
  Form,
} from 'components/Auth/RegistrationPg/RegistrationPg.styled';
import { useForm } from 'react-hook-form';
import { yupResolver } from '@hookform/resolvers/yup';
import { useDispatch } from 'react-redux';
import sprite from '../../assets/fonts/images/icons/icons-sprite.svg';
import { useState } from 'react';
const AddBoard = ({ columnId }) => {
  //   const dispatch = useDispatch();
  const [bgdImg, setBgdImg] = useState('');
  const [icon, setIcon] = useState(options[0]);

  const {
    register,
    handleSubmit,
    formState: { errors, touched = {} },
    reset,
  } = useForm({
    mode: 'onBlur',
    defaultValues: {
      text: '',
      icon: icon,
      bgdImg: bgdImg,
    },
    resolver: yupResolver(editColumnSchema),
  });

  const onSubmit = values => {
    const dashboard = {
      name: title,
      backgroundURL: bg,
      icon,
    };

    const alreadyExists = dashboards.findIndex(item => {
      const name = item.name.toLowerCase();
      const newName = dashboard.name.toLowerCase();
      return name === newName;
    });

    if (alreadyExists >= 0) {
      return `${dashboard.name} is already added to contact list`;
    } else {
      // dispatch(addDashboard(dashboard));
    }
    reset();
  };
  const iconsArray = [
    '#icon-Project',
    '#icon-star',
    '#icon-loading',
    '#icon-puzzle-piece',
    '#icon-container',
    '#icon-lightning',
    '#icon-colors',
    '#icon-hexagon',
  ];
  const handleBgDImg = url => {
    setBgdImg(url);
  };

  const handleIcon = el => {
    setIcon(el);
  };

  return (
    <Form onSubmit={handleSubmit(onSubmit)}>
      <Input
        type="text"
        id="text"
        placeholder="To do"
        {...register('text')}
        error={touched.text && errors.text && errors.text.message}
      />
      {errors.text && <Error>{errors.text.message}</Error>}
      <FormWrapper>
        <FormTitle>Icons </FormTitle>
        <RadioBtnWrapper>
          {options.map((el, idx) => (
            <IconWrapper key={idx}>
              <Icon
                className={setIcon === el ? 'active' : ''}
                onClick={() => handleIcon(el)}
                width={18}
                height={18}
              >
                <use href={sprite + el} width={18} height={18} />
              </Icon>

              <DefaultRadioBtn type="radio" value={el} name="icon" />
            </IconWrapper>
          ))}
        </RadioBtnWrapper>
      </FormWrapper>

      <FormWrapper>
        <FormTitle>Backgraunds </FormTitle>
        <RadioBtnWrapper>
          {data.map((el, idx) => (
            <label key={idx}>
              <BgcItem
                onClick={() => handleBgDImg(el.url)}
                className={selectedBg === el.url ? 'active' : ''}
              >
                {el.url !== '' && (
                  <CustomRadioBtn
                    url={el.url}
                    onClick={() => handleBgDImg(el.url)}
                    className={selectedBg === el.url ? 'active' : ''}
                  />
                )}
              </BgcItem>

              <DefaultRadioBtn type="radio" value={el.url} name="bg" />
            </label>
          ))}
        </RadioBtnWrapper>
      </FormWrapper>

      <ButtonPlus type="submit" approve="true" text="Create" />
    </Form>
  );
};
export default AddBoard;
