import React, { useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
// Assuming these are action creators
import { deleteColumn } from '../../../redux/cards/cardsReducers'; // Assuming this is an action creator
import { useNavigate } from 'react-router-dom';
import { closeMenuMode } from '../../../redux/menu/menuSlice';

const BoardItem = ({ board, index, onActive, activePojectIndex }) => {
  const dispatch = useDispatch();
  const currBoardId = useSelector(state => state.currentBoardId);
  const [open, setOpen] = useState(false);

  const handleOpen = () => setOpen(true);
  const handleClose = () => setOpen(false);
  const navigate = useNavigate();

  const handleNavigate = e => {
    if (e.target.dataset.icon) {
      return; // If the click is on an icon, return early
    }
    navigate(`/home/${board._id}`);

    dispatch(closeMenuMode());
    if (board._id !== currBoardId) {
      dispatch(deleteColumn());
    }
  };

  const checkTextLength = text => {
    const str = text.split('');

    if (str.length <= 10) {
      return str.join('');
    }
    return str.splice(0, 10).join('') + '...';
  };

  const handleDragStart = event => {
    event.preventDefault();
  };

  const handleContextMenu = event => {
    event.preventDefault();
  };

  return (
    <li
      name={board._id}
      onClick={handleNavigate}
      //   className={board._id}
      className={activePojectIndex === index ? 'active' : ''}
      draggable={false}
      onDragStart={handleDragStart}
      onContextMenu={handleContextMenu}
    >
      <div>
        {/* Uncomment and adjust SVG code if necessary */}
        {/* <svg width="18" height="18" className={css.boardIcon}>
          <use href={`${sprite}#icon-${board.icon}`} />
        </svg> */}
        <p>{board.title}</p>
      </div>
      <div>
        <button data-icon="icon"></button>
        <button data-icon="icon">
          {/* Uncomment and adjust SVG code if necessary */}
          {/* <svg
            data-icon="icon"
            className={css.iconButton}
            width="16"
            height="16"
          >
            <use data-icon="icon" href={`${sprite}#icon-trash`} />
          </svg> */}
        </button>
      </div>
    </li>
  );
};

export default BoardItem;
