import AddBoard from 'components/AddBoard/AddBoard';
import { CloseModalCross } from '../../mainModal/CloseModal';
import {
  ModalContainer,
  ModalTitle,
  ModalHeader,
  ModalBackdrop,
} from '../EditProfileModal/EditProfileModal.styled';

import { useDispatch, useSelector } from 'react-redux';
import { useEffect } from 'react';

import { closeModal } from '../../redux/modal/modalSlice';

const AddBoardModal = () => {
  const dispatch = useDispatch();
  const activeModal = useSelector(state => state.modal.activeModal);

  const closeModalOnBackdrop = e => {
    if (e.target === e.currentTarget) {
      dispatch(closeModal());
    }
  };

  useEffect(() => {
    const closeModalonEsc = e => {
      if (e.key === 'Escape') {
        dispatch(closeModal());
      }
    };
    if (activeModal === modalNames.ADD_BOARD) {
      window.addEventListener('keydown', closeModalonEsc);
    }

    return () => {
      window.removeEventListener('keydown', closeModalonEsc);
    };
  }, [activeModal, dispatch]);

  return (
    <>
      <ModalBackdrop onClick={closeModalOnBackdrop}>
        <ModalContainer>
          <ModalHeader>
            <ModalTitle>New board</ModalTitle>
            <CloseModalCross />
          </ModalHeader>
          <AddBoard />
        </ModalContainer>
      </ModalBackdrop>
    </>
  );
};
export default AddBoardModal;
