import { useForm } from 'react-hook-form';
import { useDispatch } from 'react-redux';
import { registration } from '../../redux/authorization/authReducer';
import { toast } from 'react-toastify';
import { registrationSchema } from '../../components/validation/schema';
import { Wrap, Input, Button, Form, Error } from './RegistrationPg.styled';

function Registration() {
  const dispatch = useDispatch();

  const {
    register,
    handleSubmit,
    formState: { errors, isValid },
  } = useForm({
    mode: 'onBlur', // Validate on blur
    defaultValues: {
      name: '',
      email: '',
      password: '',
    },
    registrationSchema,
  });

  const onSubmit = data => {
    dispatch(registration(data))
      .unwrap()
      .then(() => {
        toast.success('Registration successful!');
      })
      .catch(() => {
        toast.error('Please write a correct email or password!');
      });
  };

  return (
    <>
      <Form onSubmit={handleSubmit(onSubmit)}>
        <Input
          type="text"
          placeholder="Enter your name"
          id="name"
          {...register('name')}
        />
        {errors.name && <Error>{errors.name.message}</Error>}

        <Input
          type="email"
          id="email"
          placeholder="Enter your email"
          {...register('email')}
        />
        {errors.email && <Error>{errors.email.message}</Error>}

        <Wrap>
          <Input
            type="password"
            id="password"
            placeholder="Create a password"
            {...register('password')}
          />
          {errors.password && <Error>{errors.password.message}</Error>}
        </Wrap>

        <Button type="submit" disabled={!isValid}>
          Register Now
        </Button>
      </Form>
    </>
  );
}

export default Registration;
